// @ts-nocheck
"use client";
import { useMotionValue, motion, useMotionTemplate } from "motion/react";
import React from "react";
import { cn } from "@/lib/utils";

export const HeroHighlight = ({
  children,
  className,
  containerClassName,
}: {
  children: React.ReactNode;
  className?: string;
  containerClassName?: string;
}) => {
  let mouseX = useMotionValue(0);
  let mouseY = useMotionValue(0);

  function handleMouseMove({
    currentTarget,
    clientX,
    clientY,
  }: React.MouseEvent<HTMLDivElement>) {
    if (!currentTarget) return;
    let { left, top } = currentTarget.getBoundingClientRect();

    mouseX.set(clientX - left);
    mouseY.set(clientY - top);
  }

  return (
    <div
      className={cn(
        "relative h-[40rem] flex items-center bg-white dark:bg-black justify-center w-full group",
        containerClassName
      )}
      onMouseMove={handleMouseMove}
    >
      <div className="absolute inset-0 bg-dot-thick-neutral-300 dark:bg-dot-thick-neutral-800  pointer-events-none" />
      <motion.div
        className="pointer-events-none bg-dot-thick-indigo-500 dark:bg-dot-thick-indigo-500   absolute inset-0 opacity-0 transition duration-300 group-hover:opacity-100"
        style={{
          WebkitMaskImage: useMotionTemplate`
            radial-gradient(
              200px circle at ${mouseX}px ${mouseY}px,
              black 0%,
              transparent 100%
            )
          `,
          maskImage: useMotionTemplate`
            radial-gradient(
              200px circle at ${mouseX}px ${mouseY}px,
              black 0%,
              transparent 100%
            )
          `,
        }}
      />

      <div className={cn("relative z-20", className)}>{children}</div>
    </div>
  );
};

export const Highlight = ({
  children,
  className,
}: {
  children: React.ReactNode;
  className?: string;
}) => {
  return (
    <motion.span
      initial={{
        backgroundSize: "0% 100%",
      }}
      animate={{
        backgroundSize: "100% 100%",
      }}
      transition={{
        duration: 2,
        ease: "linear",
        delay: 0.5,
      }}
      style={{
        backgroundRepeat: "no-repeat",
        backgroundPosition: "left center",
        display: "inline",
      }}
      className={cn(
        `relative inline-block pb-1   px-1 rounded-lg bg-gradient-to-r from-indigo-300 to-purple-300 dark:from-indigo-500 dark:to-purple-500`,
        className
      )}
    >
      {children}
    </motion.span>
  );
};

import { ReactNode } from "react";

export interface HeroHighlightProps {
  children: ReactNode;
  className?: string;
  containerClassName?: string;
}

export interface HighlightProps {
  children: ReactNode;
  className?: string;
}

export interface HeroHighlightDemoProps {
  className?: string;
}

"use client";
import { motion } from "motion/react";
import { HeroHighlight, Highlight } from "./hero-highlight-base";
import { HeroHighlightDemoProps } from "./hero-highlight.types";

export function HeroHighlightDemo({ className }: HeroHighlightDemoProps = {}) {
  return (
    <HeroHighlight>
      <motion.h1
        initial={{
          opacity: 0,
          y: 20,
        }}
        animate={{
          opacity: 1,
          y: [20, -5, 0],
        }}
        transition={{
          duration: 0.5,
          ease: [0.4, 0.0, 0.2, 1],
        }}
        className="text-2xl px-4 md:text-4xl lg:text-5xl font-bold text-neutral-700 dark:text-white max-w-4xl leading-relaxed lg:leading-snug text-center mx-auto "
      >
        With insomnia, nothing&apos;s real. Everything is far away. Everything
        is a{" "}
        <Highlight className="text-black dark:text-white">
          copy, of a copy, of a copy.
        </Highlight>
      </motion.h1>
    </HeroHighlight>
  );
}

export function HeroHighlightPreview({ className }: HeroHighlightDemoProps = {}) {
  return (
    <HeroHighlight containerClassName="h-96 w-full relative overflow-hidden rounded-lg">
      <motion.h1
        initial={{
          opacity: 0,
          y: 20,
        }}
        animate={{
          opacity: 1,
          y: [20, -5, 0],
        }}
        transition={{
          duration: 0.5,
          ease: [0.4, 0.0, 0.2, 1],
        }}
        className="text-xl px-4 md:text-2xl lg:text-3xl font-bold text-neutral-700 dark:text-white max-w-2xl leading-relaxed lg:leading-snug text-center mx-auto"
      >
        Beautiful{" "}
        <Highlight className="text-black dark:text-white">
          highlighted text
        </Highlight>{" "}
        with animated background
      </motion.h1>
    </HeroHighlight>
  );
}

export function HeroHighlightMinimal({ className }: HeroHighlightDemoProps = {}) {
  return (
    <HeroHighlight containerClassName="h-96 w-full relative overflow-hidden rounded-lg">
      <h1 className="text-2xl md:text-3xl font-bold text-neutral-700 dark:text-white text-center">
        Simple text with{" "}
        <Highlight>
          highlight effect
        </Highlight>
      </h1>
    </HeroHighlight>
  );
}

export function HeroHighlightMultiple({ className }: HeroHighlightDemoProps = {}) {
  return (
    <HeroHighlight containerClassName="h-96 w-full relative overflow-hidden rounded-lg">
      <motion.div
        initial={{
          opacity: 0,
          y: 20,
        }}
        animate={{
          opacity: 1,
          y: 0,
        }}
        transition={{
          duration: 0.5,
          ease: [0.4, 0.0, 0.2, 1],
        }}
        className="text-center space-y-4"
      >
        <h2 className="text-2xl md:text-3xl font-bold text-neutral-700 dark:text-white">
          <Highlight>Build</Highlight> amazing products
        </h2>
        <p className="text-lg text-neutral-600 dark:text-neutral-300 max-w-2xl mx-auto">
          Create stunning interfaces with{" "}
          <Highlight className="text-black dark:text-white">
            animated highlights
          </Highlight>{" "}
          that capture attention and{" "}
          <Highlight className="text-black dark:text-white">
            delight users
          </Highlight>
        </p>
      </motion.div>
    </HeroHighlight>
  );
}

export function HeroHighlightCallToAction({ className }: HeroHighlightDemoProps = {}) {
  return (
    <HeroHighlight containerClassName="h-96 w-full relative overflow-hidden rounded-lg">
      <motion.div
        initial={{
          opacity: 0,
          y: 20,
        }}
        animate={{
          opacity: 1,
          y: 0,
        }}
        transition={{
          duration: 0.5,
          ease: [0.4, 0.0, 0.2, 1],
        }}
        className="text-center space-y-6"
      >
        <h1 className="text-3xl md:text-4xl font-bold text-neutral-700 dark:text-white">
          Ready to{" "}
          <Highlight className="text-black dark:text-white">
            transform your ideas
          </Highlight>
          ?
        </h1>
        <p className="text-lg text-neutral-600 dark:text-neutral-300 max-w-xl mx-auto">
          Join thousands of developers building the future
        </p>
        <button className="px-8 py-3 bg-black dark:bg-white text-white dark:text-black rounded-full font-medium hover:scale-105 transition-transform">
          Get Started Free
        </button>
      </motion.div>
    </HeroHighlight>
  );
}

export { HeroHighlight, Highlight } from "./hero-highlight-base";
export { 
  HeroHighlightDemo,
  HeroHighlightPreview,
  HeroHighlightMinimal,
  HeroHighlightMultiple,
  HeroHighlightCallToAction
} from "./hero-highlight-demo";
export type { 
  HeroHighlightProps, 
  HighlightProps,
  HeroHighlightDemoProps 
} from "./hero-highlight.types";
